DROP TABLE IF EXISTS `cms_content`;
CREATE TABLE IF NOT EXISTS `cms_content` (
  `id` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '//ID',
  `title` varchar(50) NOT NULL COMMENT '//标题',
  `nav` mediumint(8) UNSIGNED NOT NULL COMMENT '//栏目号',
  `attr` varchar(20) DEFAULT NULL COMMENT '//属性',
  `tag` varchar(30) DEFAULT NULL COMMENT '//标签',
  `keyword` varchar(30) DEFAULT NULL COMMENT '//关键字',
  `thumbnail` varchar(100) DEFAULT NULL COMMENT '//缩略图',
  `source` varchar(20) DEFAULT NULL COMMENT '//文章来源',
  `author` varchar(10) DEFAULT NULL COMMENT '//作者',
  `info` varchar(200) DEFAULT NULL COMMENT '//简介',
  `content` text NOT NULL COMMENT '//详细内容',
  `commend` tinyint(1) NOT NULL DEFAULT '1' COMMENT '//是否允许评论',
  `count` smallint(6) NOT NULL DEFAULT '0' COMMENT '//浏览次数',
  `gold` smallint(6) NOT NULL DEFAULT '0' COMMENT '//消费金币',
  `sort` tinyint(1) NOT NULL DEFAULT '0' COMMENT '//排序',
  `readlimit` tinyint(1) DEFAULT '0' COMMENT '//阅读权限',
  `color` varchar(10) NOT NULL COMMENT '//颜色',
  `date` datetime NOT NULL COMMENT '//发布时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

