--
-- 表的结构 `cms_nav`
--

DROP TABLE IF EXISTS `cms_nav`;
CREATE TABLE IF NOT EXISTS `cms_nav` (
  `id` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '//ID',
  `nav_name` varchar(20) NOT NULL COMMENT '//导航名称',
